import React from 'react'
import { View, Text, Image } from 'react-native';
import { theme, text } from '../core/styles';

import draw from '../core/assets/draw.png';
import arrow from '../core/assets/arrow.png';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';

const Home: React.FC = () => {
    const navigation = useNavigation();

    return (<View style={theme.container}>
        <View style={theme.card}>
            <Image source={draw} style={theme.draw}/>
            <View style={theme.textContainer}>
                <Text style={text.bold}>Conheça o melhor catálogo de produtos</Text>
                <Text style={text.regular}>Ajudaremos você a encontrar os melhores produtos disponíveis no mercado.</Text>
            </View>
            <TouchableOpacity style={theme.primaryButton} activeOpacity={0.8} 
            onPress={() => navigation.navigate('Catalog')}>
                <Text style={text.primaryText}>INICIE AGORA SUA BUSCA</Text>
                <View style={theme.arrowContainer}>
                    <Image source={arrow}/>
                </View>
            </TouchableOpacity>
        </View>
    </View>)

}

export default Home;
